import "./App.css";
import NavBar from "./NavBar";
import React, { Component } from "react";
import News from "./News= setProgress = {setProgress}";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import LoadingBar from 'react-top-loading-bar'

export default class App extends Component {
  render() {
    state = {
      progress:0
    }
    const setProgress = (progress) =>
    {
      this.setState({progress: this.state.progress})
    }
    return (
      <Router>
        <div>
        <LoadingBar
        color='#f11946'
        progress={this.state.progress}/>
          <NavBar />
          <Routes>
          <Route
              path="/"
              element={<News setProgress = {setProgress} key="general" pageSize={9} category="general"/>}
            />
            <Route
              path="/general"
              element={<News setProgress = {setProgress} key="general" pageSize={9} category="general" />}
            />
            <Route
              path="/business"
              element={<News setProgress = {setProgress} key="business" pageSize={9} category="business" />}
            />
            <Route
              path="/entertainment"
              element={
                <News setProgress = {setProgress}
                  key="entertainment"
                  pageSize={9}
                  category="entertainment"
                />
              }
            />
            <Route
              path="/sports"
              element={<News setProgress = {setProgress} key="sports" pageSize={9} category="sports" />}
            />
            <Route
              key="health"
              path="/health"
              element={<News setProgress = {setProgress} pageSize={9} category="health" />}
            />
            <Route
              path="/science"
              element={<News setProgress = {setProgress} key="science" pageSize={9} category="science" />}
            />
            <Route
              path="/technology"
              element={
                <News setProgress = {setProgress} key="technology" pageSize={9} category="technology" />
              }
            />
          </Routes>
        </div>
      </Router>
    );
  }
}
